const fetchUser = user => {
    return (dispatch) => {
        fetch("http://localhost:3000/users/", {
      crossDomain: true, 
      method: 'GET',
      headers: {'Content-Type': 'application/json'},
    })
    .then(res => res.json())
    .then(user => {
        dispatch({type:"FETCH_USER", payload: user})
    })
    }
}

export default fetchUser;
