import React from "react";
import pikachu from "./funny-pikachu.png";
import steamApiKey from '../keys/keys'

export default class UserBox extends React.Component {
  constructor() {
    super();
    this.state = {
      profilePic: `${pikachu}`,
      username: "COVID-Pikachu"
    }
  }


  componentDidMount(){
    const userEndpoint = `/ISteamUser/GetPlayerSummaries/v0002/?key=${steamApiKey}&steamids=76561198036778665`
    fetch(userEndpoint, {
      crossDomain: true, 
      method: 'GET',
      headers: {'Content-Type': 'application/json'},
    })
    .then(res => res.json())
    .then(json => {
      this.setState({data: json.response.players[0]})
    })
  }

  render() {
    console.log(this.state.data);
    if (this.state) { return (
      <div className="profile-box">
        <img src={ this.state.avatarmedium } alt="Pikachu staying safe" />
        <h3>{ this.state.personaname }</h3>
      </div>
    );
  }
}
}
